jQuery(document).ready(function($){$(window).scroll(function(){var top_of_element=$(".scroll-fill-container").offset().top;var bottom_of_element=$(".scroll-fill-container").offset().top+$(".scroll-fill-container").outerHeight();var bottom_of_screen=$(window).scrollTop()+$(window).innerHeight();var top_of_screen=$(window).scrollTop();var header_offset=$(".featured-cta").height()+$(".site-header").height();if(bottom_of_element<bottom_of_screen&&top_of_element>top_of_screen+header_offset){var el_pos=bottom_of_screen-bottom_of_element;var viewport_height=$(window).height()-header_offset-$(".scroll-fill-container").outerHeight();var before_percent=((el_pos/viewport_height)*100)-1;var after_percent=(el_pos/viewport_height)*100;$(".scroll-fill p").css("background","linear-gradient(0deg, white 0%, white "+before_percent+"%, transparent "+after_percent+"%, transparent 100%)");}});});